mapper.select:
select count(user_id)
from users
where user_id = #{userid}

select user_id, user_name
from users
where user_id = #{userid} and user_password = #{userpwd}

select user_id, user_name, user_password, email_id, email_domain, join_date
from users
where user_id = #{userId}

